UPDATE `oxcontents` SET `OXCONTENT`='Folgende Artikel wurden soeben unter [{ $shop->oxshops__oxname->value }] bestellt:', `OXCONTENT_1`='The following products have been ordered in [{ $shop->oxshops__oxname->value }] right now:' WHERE `OXLOADID`='oxadminorderemail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<strong class=\'warning\'>Hinweis:</strong> Derzeit ist keine Liefermethode für dieses Land bekannt. Bitte Liefermöglichkeiten suchen und den Besteller unter Angabe der <strong>Lieferkosten</strong> informieren!\r\n<br><br>\r\n\r\nFolgende Artikel wurden soeben unter [{ $shop->oxshops__oxname->value }] bestellt:', `OXCONTENT_1`='<strong class=\'warning\'>Information:</strong> Currently, there is no shipping method defined for this country. Please find a delivery option and inform the customer about the <strong>shipping costs</strong>.\r\n<br><br>\r\n\r\nThe following products have been ordered on [{ $shop->oxshops__oxname->value }]:' WHERE `OXLOADID`='oxadminordernpemail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<h1>Anmeldung zum Newsletter</h1>\r\n\r\nHallo [{ $user->oxuser__oxsal->value|oxmultilangsal }] [{ $user->oxuser__oxfname->value }] [{ $user->oxuser__oxlname->value }],\r\n\r\n<br><br>\r\n\r\n<p>vielen Dank für Ihre Anmeldung zu unserem Newsletter.</p><br>\r\n\r\n<p>Um den Newsletter freizuschalten klicken Sie bitte auf folgenden Link:</p>\r\n\r\n[{include file=$oViewConf->getTemplatePath(\"inc/button.tpl\") url=$subscribeLink text=\"jetzt freischalten\"}]\r\n<br>\r\n\r\nIhr [{ $shop->oxshops__oxname->value }] Team', `OXCONTENT_1`='<h1>Newsletter subscription</h1>\r\n\r\nHello [{ $user->oxuser__oxsal->value|oxmultilangsal }] [{ $user->oxuser__oxfname->value }] [{ $user->oxuser__oxlname->value }],<br><br>\r\n\r\n<p>thank you for your newsletter subscription.</p><br>\r\n\r\nFor final registration, please click on this link:<br>\r\n\r\n[{include file=$oViewConf->getTemplatePath(\"inc/button.tpl\") url=$subscribeLink text=\"register now\"}]\r\n<br>\r\n\r\nYour [{ $shop->oxshops__oxname->value }] Team' WHERE `OXLOADID`='oxnewsletteremail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<h1>Versandbestätigung</h1>\r\n\r\nGuten Tag, [{ $order->oxorder__oxbillsal->value|oxmultilangsal }] [{ $order->oxorder__oxbillfname->value }] [{ $order->oxorder__oxbilllname->value }],\r\n\r\n<p>unser Vertriebszentrum hat soeben folgende Artikel versandt.</p>', `OXCONTENT_1`='<h1>Shipping notification</h1>\r\n\r\nHello [{ $order->oxorder__oxbillsal->value|oxmultilangsal }] [{ $order->oxorder__oxbillfname->value }] [{ $order->oxorder__oxbilllname->value }],\r\n\r\n<p>our distribution center just shipped this product:</p>' WHERE `OXLOADID`='oxordersendemail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<h1>Preisalarm</h1>\r\n\r\nPreisalarm im [{ $shop->oxshops__oxname->value }]!<br>\r\n<br>\r\n[{ $email }] bietet für Artikel <strong>[{ $product->oxarticles__oxtitle->value }]</strong>, Artnum. [{ $product->oxarticles__oxartnum->value }]<br>\r\n<br>\r\nOriginalpreis: [{ $product->getFPrice() }] [{ $currency->name}]<br>\r\nGEBOTEN: [{ $bidprice }] [{ $currency->name}]<br>\r\n<br>\r\n<br>\r\nIhr Shop.<br>', `OXCONTENT_1`='<h1>Price alert</h1>\r\n\r\nPrice alert at [{ $shop->oxshops__oxname->value }]!<br>\r\n<br>\r\n[{ $email }] bids for product <strong>[{ $product->oxarticles__oxtitle->value }]</strong>, product # [{ $product->oxarticles__oxartnum->value }]<br>\r\n<br>\r\nOriginal price: [{ $currency->name}][{ $product->getFPrice() }]<br>\r\nBid: [{ $currency->name}][{ $bidprice }]<br>\r\n<br>\r\n<br>\r\nYour store<br>' WHERE `OXLOADID`='oxpricealarmemail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<h1>Danke für Ihre Registrierung</h1>\r\n\r\nHallo [{ $user->oxuser__oxsal->value|oxmultilangsal }] [{ $user->oxuser__oxfname->value }] [{ $user->oxuser__oxlname->value }], \r\n<br><br>\r\n\r\n<p>vielen Dank für Ihre Registrierung bei [{ $shop->oxshops__oxname->value }]!</p>\r\n<p>Sie können sich ab sofort auch mit Ihrer Kundennummer <strong>[{ $user->oxuser__oxcustnr->value }]</strong> einloggen.</p>\r\n\r\n<br>\r\nIhr [{ $shop->oxshops__oxname->value }] Team', `OXCONTENT_1`='<h1>Thank you for your registration</h1>\r\n\r\nHello [{ $user->oxuser__oxsal->value|oxmultilangsal }] [{ $user->oxuser__oxfname->value }] [{ $user->oxuser__oxlname->value }],\r\n<br><br>\r\n\r\n<p>thank you for your registration at [{ $shop->oxshops__oxname->value }]!</p>\r\n<p>From now on, you can log in with your customer number <strong>[{ $user->oxuser__oxcustnr->value }]</strong>.</p>\r\n<br>\r\nYour [{ $shop->oxshops__oxname->value }] team<br>' WHERE `OXLOADID`='oxregisteremail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<h1>Haben Sie Ihr Passwort vergessen?</h1>\r\n\r\nHallo [{ $user->oxuser__oxsal->value|oxmultilangsal }] [{ $user->oxuser__oxfname->value }] [{ $user->oxuser__oxlname->value }],\r\n<br><br>\r\n\r\nöffnen Sie den folgenden Link, um ein neues Passwort für [{ $shop->oxshops__oxname->value }] einzurichten:<br>\r\n\r\n[{include file=$oViewConf->getTemplatePath(\"inc/button.tpl\") url=$oViewConf->getBaseDir()|cat:\'index.php?cl=forgotpwd&uid=\'|cat:$user->getUpdateId()|cat:\'&amp;lang=\'|cat:$oViewConf->getActLanguageId()|cat:\'&shp=\'|cat:$shop->oxshops__oxid->value text=\'neues Passwort\'}]\r\n\r\n<p>Diesen Link können Sie innerhalb der nächsten [{ $user->getUpdateLinkTerm()/3600 }] Stunden aufrufen.</p><br>\r\n\r\nIhr [{ $shop->oxshops__oxname->value }] Team', `OXCONTENT_1`='<h1>Did you forget your password?</h1>\r\n\r\nHello [{ $user->oxuser__oxsal->value|oxmultilangsal }] [{ $user->oxuser__oxfname->value }] [{ $user->oxuser__oxlname->value }],\r\n<br><br>\r\n\r\nfollow this link to generate a new password for [{ $shop->oxshops__oxname->value }]:<br>\r\n\r\n[{include file=$oViewConf->getTemplatePath(\"inc/button.tpl\") url=$oViewConf->getBaseDir()|cat:\'index.php?cl=forgotpwd&uid=\'|cat:$user->getUpdateId()|cat:\'&amp;lang=\'|cat:$oViewConf->getActLanguageId()|cat:\'&shp=\'|cat:$shop->oxshops__oxid->value text=\'new password\'}]\r\n\r\n<p>You can use this link within the next [{ $user->getUpdateLinkTerm()/3600 }] hours.</p><br>\r\n\r\nYour [{ $shop->oxshops__oxname->value }] team' WHERE `OXLOADID`='oxupdatepassinfoemail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<h1>Vielen Dank für Ihre Bestellung!</h1>\r\n\r\nNachfolgend haben wir zur Kontrolle Ihre Bestellung noch einmal aufgelistet.<br>\r\n\r\n<p>Bei Fragen sind wir jederzeit für Sie da: Schreiben Sie einfach an [{ $shop->oxshops__oxorderemail->value }]!</p>', `OXCONTENT_1`='<h1>Thank you for your order!</h1>\r\n\r\nBelow, we have listed your order.<br>\r\n\r\n<p>If you have any questions, don\'t hesitate to drop us an e-mail [{ $shop->oxshops__oxorderemail->value }]!</p>' WHERE `OXLOADID`='oxuserorderemail' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<p>Fügen Sie hier Ihre Widerrufsbelehrung ein.</p>', `OXCONTENT_1`='<p>Right to Withdrawal can be inserted here.</p>' WHERE `OXLOADID`='oxuserorderemailend' AND `OXSHOPID`='oxbaseshop';

UPDATE `oxcontents` SET `OXCONTENT`='<h1>Vielen Dank für Ihre Bestellung!</h1>\r\n\r\n<p><strong>Hinweis:</strong> Derzeit ist uns keine Versandmethode für dieses Land bekannt. Wir werden versuchen, Versandmethoden zu finden und Sie über das Ergebnis unter Angabe der Versandkosten informieren.</p><br>\r\n\r\n<p>Bei Fragen sind wir jederzeit für Sie da: Schreiben Sie einfach an [{ $shop->oxshops__oxorderemail->value }]!</p>', `OXCONTENT_1`='<h1>Thank you for your order!</h1>\r\n\r\n<p><strong>Information:</strong> Currently, there is no shipping method defined for your country. We will find a method to deliver the goods you purchased and will inform you as soon as possible.\r\n</p>\r\n\r\n<p>If you have any requests, don\'t hesitate to contact us! [{ $shop->oxshops__oxorderemail->value }]</p>' WHERE `OXLOADID`='oxuserordernpemail' AND `OXSHOPID`='oxbaseshop';

